mapper.select:
select
distinct
from t_ev_card_history tech where 1 = 1
order by ${orderByClause}
limit #{currentSize} , #{pageSize}

mapper.update:
update t_ev_card_history tech
tech.GUID_ = #{guid,jdbcType=VARCHAR}, tech.CARD_NO = #{cardNo,jdbcType=VARCHAR}, tech.CAR_NO = #{carNo,jdbcType=VARCHAR}, tech.COMPANY_ID = #{companyId,jdbcType=VARCHAR}, tech.COMPANY_NAME = #{companyName,jdbcType=VARCHAR}, tech.OWNER_ID = #{ownerId,jdbcType=VARCHAR}, tech.OWNER_NAME = #{ownerName,jdbcType=VARCHAR}, tech.CARD_OLD_BALANCE = #{cardOldBalance,jdbcType=NUMERIC}, tech.CARD_MONEY = #{cardMoney,jdbcType=NUMERIC}, tech.CARD_BALANCE = #{cardBalance,jdbcType=NUMERIC}, tech.CARD_MONEY_YH = #{cardMoneyYh,jdbcType=NUMERIC}, tech.CARD_MONEY_TZ = #{cardMoneyTz,jdbcType=NUMERIC}, tech.CARD_MONEY_JF = #{cardMoneyJf,jdbcType=NUMERIC}, tech.CARD_TYPE = #{cardType,jdbcType=VARCHAR}, tech.CARD_STATUS = #{cardStatus,jdbcType=VARCHAR}, tech.OPERATOR_TYPE = #{operatorType,jdbcType=VARCHAR}, tech.OPERATOR_ID = #{operatorId,jdbcType=VARCHAR}, tech.OPERATOR_NAME = #{operatorName,jdbcType=VARCHAR}, tech.OPERATE_TIME = #{operateTime,jdbcType=VARCHAR},
where 1 = 1 and tech.GUID_ = #{guid,jdbcType=VARCHAR}
