mapper.select:
select
distinct
from tb_base_station tbs where 1 = 1
order by ${orderByClause}
limit #{currentSize} , #{pageSize}

mapper.update:
update tb_base_station tbs
tbs.GUID_ = #{guid,jdbcType=VARCHAR}, tbs.NAME_ = #{name,jdbcType=VARCHAR}, tbs.SHORT_NAME = #{shortName,jdbcType=VARCHAR}, tbs.CODE_ = #{code,jdbcType=VARCHAR}, tbs.REF_CODE = #{refCode,jdbcType=VARCHAR}, tbs.INDUSTRY_CODE = #{industryCode,jdbcType=VARCHAR}, tbs.INDUSTRY_NAME = #{industryName,jdbcType=VARCHAR}, tbs.AREA_ID = #{areaId,jdbcType=VARCHAR}, tbs.AREA_CODE = #{areaCode,jdbcType=VARCHAR}, tbs.AREA_NAME = #{areaName,jdbcType=VARCHAR}, tbs.COMPANY_ID = #{companyId,jdbcType=VARCHAR}, tbs.ADDRESS_ = #{address,jdbcType=VARCHAR}, tbs.LON = #{lon,jdbcType=VARCHAR}, tbs.LAT = #{lat,jdbcType=VARCHAR}, tbs.PICTURE_PATH = #{picturePath,jdbcType=VARCHAR}, tbs.MANAGEMENT_COMPANY = #{managementCompany,jdbcType=VARCHAR}, tbs.COMPLETED_DATE = #{completedDate,jdbcType=VARCHAR}, tbs.CONTACT_PERSON = #{contactPerson,jdbcType=VARCHAR}, tbs.CONTACT_TEL = #{contactTel,jdbcType=VARCHAR}, tbs.RUNSTATUS = #{runstatus,jdbcType=VARCHAR}, tbs.RUN_DAYS = #{runDays,jdbcType=VARCHAR}, tbs.STATUS_ = #{status,jdbcType=VARCHAR},
where 1 = 1 and tbs.GUID_ = #{guid,jdbcType=VARCHAR}
